INSERT INTO `products` VALUES (21,'product',NULL,'2020-09-10 03:07:32','2020-09-24 04:47:42','Máy Lọc Nước RO - 1000',1,NULL,1,1,'<p><span style=\"font-size:16px\"><span style=\"color:#000000\">RO-1000<br />\r\nT&iacute;nh năng &amp; Lợi &iacute;ch<br />\r\nThiết kế gọn gang thanh lịch ph&ugrave; hợp cho quầy b&agrave;n.<br />\r\nKết hợp với bộ lọc nội tuyến dễ d&agrave;ng thay thế v&agrave; bảo tr&igrave;<br />\r\nThiết lập tất cả trong một ~ kệ v&agrave; b&igrave;nh nước. &nbsp;Bộ lọc DP l&agrave; t&ugrave;y chọn.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"color:#000000\"><img alt=\"\" height=\"650\" src=\"/public/uploads/images/ROO%201000.png\" width=\"672\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"color:#000000\"><img alt=\"\" height=\"600\" src=\"/public/uploads/images/Screenshot%20at%20Sep%2011%2010-44-48.png\" width=\"504\" /><br />\r\n<img alt=\"\" height=\"166\" src=\"/public/uploads/images/Screenshot%20at%20Sep%2011%2010-45-44.png\" width=\"615\" /></span></span></p>','counter-top-he-thong-case-ro','Máy Lọc Nước RO - 1000','Counter Top HỆ THỐNG CASE RO','Counter Top HỆ THỐNG CASE RO',NULL,NULL,'swun yuan.png',NULL,'swun yuan.png','Published',8,'0',0,'RO-1000','0'),(22,'product',NULL,'2020-09-11 03:52:28','2020-09-24 04:47:50','Máy Lọc Nước RO - 2000',1,NULL,1,1,'<p><span style=\"color:#000000\"><span style=\"font-size:16px\">T&iacute;nh năng &amp; Lợi &iacute;ch<br />\r\nThiết kế nhỏ gọn thanh lịch<br />\r\nThiết kế treo tường tối ưu h&oacute;a kh&ocirc;ng gian.<br />\r\nTất cả trong một counter top and water &nbsp;storage.<br />\r\nDễ lắp đặt.<br />\r\nBộ lọc nhanh DP &nbsp;dễ thay đổi. &nbsp;<br />\r\nNh&atilde;n tủy chỉnh c&oacute; thể thay đổi.</span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:16px\"><img alt=\"\" height=\"395\" src=\"/public/uploads/images/RO%202000%201.png\" width=\"355\" /></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:16px\"><img alt=\"\" height=\"166\" src=\"/public/uploads/images/Screenshot%20at%20Sep%2011%2010-45-44.png\" width=\"615\" /></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:16px\"><img alt=\"\" height=\"310\" src=\"/public/uploads/images/RO%203000.png\" width=\"806\" /></span></span></p>','thiet-ke-treo-tuong-he-thong-ro','Máy Lọc Nước RO - 2000','Thiết kế treo tường HỆ THỐNG RO','Thiết kế treo tường HỆ THỐNG RO',NULL,NULL,'RO 2000.png',NULL,'RO 2000.png','Published',8,'0',0,'RO- 2000','0'),(23,'product',NULL,'2020-09-11 03:57:01','2020-09-24 04:47:59','Máy Lọc Nước RO - 5000',1,NULL,1,1,'<p><span style=\"font-size:16px\"><span style=\"color:#000000\">T&iacute;nh năng &amp; Lợi &iacute;ch<br />\r\nTuyệt vời để kết hợp với thiết kế tinh tế, đa dạng với hệ thống RO truyền thống.<br />\r\nBảng điều khiển th&ocirc;ng minh: điều khiển d&ograve;ng vi t&iacute;nh vi m&ocirc;, chế độ tự động hiển thị để thay thế c&aacute;c bộ lọc.<br />\r\nThiết kế chổng rỉ: cấu tr&uacute;c nhựa để chống sự cố từ khu&ocirc;n th&eacute;p.<br />\r\nDung lượng: &nbsp;tối đa đến 250G.<br />\r\nBộ lọc chất lượng cao: Vỏ dừa chất lượng cao k&iacute;ch hoạt bộ lọc Carbon-để nước c&oacute; vị ngon v&agrave; tinh khiết.<br />\r\nTắt nước khẩn cấp khi ph&aacute;t hiện r&ograve; rỉ.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"color:#000000\"><img alt=\"\" height=\"451\" src=\"/public/uploads/images/RO%205000%202.png\" width=\"409\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"color:#000000\"><img alt=\"\" height=\"195\" src=\"/public/uploads/images/RO%205000%203.png\" width=\"395\" /></span></span></p>\r\n\r\n<p><img alt=\"\" height=\"518\" src=\"/public/uploads/images/Ma%CC%80ng%20co%CC%82ng%20nghie%CC%A3%CC%82p.png\" width=\"762\" /><br />\r\n<img alt=\"\" height=\"623\" src=\"/public/uploads/images/Screenshot%20at%20Sep%2011%2010-59-00.png\" width=\"714\" /></p>','he-thong-thong-minh-ro-de-duoi-quay','Máy Lọc Nước RO - 5000','Hệ thống thông minh RO để dưới quầy','Hệ thống thông minh RO để dưới quầy',NULL,NULL,'RO 5000.png',NULL,'RO 5000.png','Published',9,'0',0,'RO- 5000','0'),(24,'product',NULL,'2020-09-11 04:10:16','2020-10-05 03:02:36','MÀNG RO',1,NULL,1,1,'<p><span style=\"color:#000000\"><span style=\"font-size:16px\">Giới hạn hoạt động</span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:16px\"><img alt=\"\" height=\"148\" src=\"/public/uploads/images/Screenshot%20at%20Sep%2011%2011-05-51.png\" width=\"775\" /></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:16px\">Nhiệt độ tối đa để hoạt động li&ecirc;n tục cho nồng độ pH &gt; 10 l&agrave; 35℃(95℉)<br />\r\nTrong một số điều kiện nhất định, sự hiện diện của Chlorine tự do v&agrave; c&aacute;c t&aacute;c nh&acirc;n g&acirc;y oxy h&oacute;a sẽ sớm l&agrave;m hỏng m&agrave;ng. V&igrave; thiệt hại oxy h&oacute;a kh&ocirc;ng được bảo h&agrave;nh, SWUNYUAN FILMTECH khuyến kh&iacute;ch loại bỏ Chlorine tự do bằng phương ph&aacute;p tiền xủ l&yacute; trước khi tiếp x&uacute;c với m&agrave;ng.</span><br />\r\n<img alt=\"\" height=\"475\" src=\"/public/uploads/images/MA%CC%80NG%20RO%203.png\" width=\"915\" /><img alt=\"\" height=\"348\" src=\"/public/uploads/images/MA%CC%80NG%20RO%204.png\" width=\"800\" /></span></p>\r\n\r\n<p><span style=\"color:#000000\"><img alt=\"\" height=\"464\" src=\"/public/uploads/images/MA%CC%80NG%20RO%207.png\" width=\"800\" /></span></p>\r\n\r\n<p><span style=\"color:#000000\"><img alt=\"\" height=\"289\" src=\"/public/uploads/images/MA%CC%80NG%20RO%208.png\" width=\"976\" /></span></p>','mang-ro','MÀNG RO','MÀNG RO','MÀNG RO',NULL,NULL,'MÀNG RO.png',NULL,'MÀNG RO.png','Published',8,'0',0,'MÀNG RO','0'),(25,'product',NULL,'2020-09-11 04:31:57','2020-09-24 04:47:30','Bình RO',1,NULL,1,1,'<p>T&iacute;nh năng &amp; Lợi &iacute;ch<br />\r\nVỏ b&igrave;nh l&agrave;m từ polypropylene bền để đảm bảo bề mặt kh&ocirc;ng bị rỉ s&eacute;t v&agrave; mịn m&agrave;ng.<br />\r\nChất liệu đạt chuẩn FDA l&agrave;m nước uống.<br />\r\nTất cả c&aacute;c chất liệu nhựa, kh&ocirc;ng bộ phận kim loại n&agrave;o tiếp x&uacute;c trực tiếp với nước để đề ph&ograve;ng rỉ s&eacute;t. &nbsp;<br />\r\nM&agrave;ng ngăn được l&agrave;m từ chất liệu đạt chuẩn FDA để ngăn m&ugrave;i kh&oacute; chịu của m&agrave;ng cao su th&ocirc;ng thường.<br />\r\nChứng nhận NSF-58.<br />\r\n&Aacute;p suất kh&ocirc;ng kh&iacute; trong b&igrave;nh c&oacute; thể được nạp bởi dụng cụ bơm tay.<br />\r\nVỏ nhựa đảm bảo b&igrave;nh kh&ocirc;ng bị rỉ s&eacute;t bởi độ ẩm tr&ecirc;n bề mặt hoặc dưới bồn rửa</p>\r\n\r\n<p><img alt=\"\" height=\"381\" src=\"/public/uploads/images/Screenshot%20at%20Sep%2011%2011-20-21.png\" width=\"910\" /></p>','binh-ro','Bình RO','Bình RO','Bình RO',NULL,NULL,'BÌNH RO.png',NULL,'BÌNH RO.png','Published',7,'0',0,'Z-TK022 / Z-TK032','0'),(26,'product',NULL,'2020-09-11 04:34:01','2020-10-01 18:22:37','ỐNG LLDPE',1,NULL,1,1,'<p>D&Ograve;NG ỐNG LLDPE<br />\r\nT&iacute;nh năng &amp; Lợi &iacute;ch:<br />\r\nPolyethylene tuyến t&iacute;nh mật độ thấp. &nbsp;Chắc chắn, linh hoạt, v&agrave; bền bỉ.<br />\r\nChất liệu tu&acirc;n thủ FDA. &nbsp;<br />\r\nChứng nhận NSF-61.<br />\r\nĐộ d&agrave;i: đa dạng. &nbsp;<br />\r\n&Aacute;p suất hoạt động ti&ecirc;u chuẩn:<br />\r\nDưới 125PSI đối với độ d&agrave;i ống l&agrave; 1/4&rdquo;, 3/8&rdquo; O.D. &nbsp;Dưới 90PSI với độ d&agrave;i ốn 1/2&rdquo; tube O.D.<br />\r\nNhiệt độ hoạt động: 4&deg;C ~ 42&deg;C.<br />\r\nChất liệu nylon t&ugrave;y nhu cầu sử dụng v&agrave; kh&ocirc;ng bị liệt k&ecirc; bởi NSF.<br />\r\n<img alt=\"\" height=\"542\" src=\"/public/uploads/images/Screenshot%20at%20Sep%2011%2011-33-06.png\" width=\"577\" /></p>\r\n\r\n<p><img alt=\"\" height=\"381\" src=\"/public/uploads/images/Screenshot%20at%20Sep%2011%2011-33-23.png\" width=\"927\" /></p>','ong-lldpe','ỐNG LLDPE','ỐNG LLDPE','ỐNG LLDPE',NULL,NULL,'ỐNG LLDPE.png',NULL,'placeholder.png','Published',7,'0',0,'LLDPE','0'),(27,'product',NULL,'2020-09-11 04:39:31','2020-09-24 04:47:12','BỘ LỌC PP CÓ RÃNH VÀ RÃNH NHỎ',1,NULL,1,1,'<p><span style=\"color:#000000\"><span style=\"font-size:16px\">D&Ograve;NG FGC-LL-WX-ZZZ</span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:16px\">C&oacute; r&atilde;nh tr&ecirc;n v&agrave; b&ecirc;n ngo&agrave;i bề mặt.<br />\r\nHộp mực c&oacute; cấu tr&uacute;c 3 lớp, khả năng trử bụi cao, tuổi thọ bộ lọc cao.<br />\r\nKhối lượng rỗng cao, dẫn đến ch&ecirc;nh lệch &aacute;p suất thấp v&agrave; khả năng gữ bụi cao.<br />\r\nKh&ocirc;ng c&oacute; nhựa, chất kết d&iacute;nh hoặc chất chống tĩnh điện để đ&aacute;m bảo khả năng tr&iacute;ch xuất tối thiểu. &nbsp;<br />\r\nĐược tạo n&ecirc;n bởi li&ecirc;n kết điện m&agrave; kh&ocirc;ng cần vật kết nối hay chất kết d&iacute;nh.<br />\r\nĐược chứng nhận bởi NSF-42.</span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:16px\">D&Ograve;NG FBC-LL-WX-ZZZ<br />\r\nT&iacute;nh năng &amp; Lợi &iacute;ch<br />\r\nC&oacute; r&atilde;nh nhỏ tr&ecirc;n, ngo&agrave;i bề mặt.<br />\r\nHộp mực c&oacute; cấu tr&uacute;c 3 lớp, khả năng trử bụi cao, tuổi thọ bộ lọc cao.<br />\r\nPP tương th&iacute;ch 100% với một loạt chất lỏng trong qu&aacute; tr&igrave;nh hoạt động. &nbsp;<br />\r\nSợi lọc tự tan Micro-Denier, c&oacute; khả năng đ&agrave;o thải cao.<br />\r\nĐược tạo n&ecirc;n bởi li&ecirc;n kết điện m&agrave; kh&ocirc;ng cần vật kết nối hay chất kết d&iacute;nh.<br />\r\nĐược chứng nhận bởi NSF-42.<br />\r\n<img alt=\"\" height=\"451\" src=\"/public/uploads/images/Screenshot%20at%20Sep%2011%2011-39-03.png\" width=\"896\" /></span></span></p>','bo-loc-pp-co-ranh-va-ranh-nho','BỘ LỌC PP CÓ RÃNH VÀ RÃNH NHỎ','BỘ LỌC PP CÓ RÃNH VÀ RÃNH NHỎ','BỘ LỌC PP CÓ RÃNH VÀ RÃNH NHỎ',NULL,NULL,'BỘ LỌC PP CÓ RÃNH VÀ RÃNH NHỎ.png',NULL,'placeholder.png','Published',5,'0',0,'DÒNG FGC-LL-WX-ZZZ,DÒNG FBC-LL-WX-ZZZ','0'),(28,'product',NULL,'2